// Extract order information
const order = $json.body;
const orderData = {
  orderId: order.id,
  customerName: order.billing.first_name + ' ' + order.billing.last_name,
  customerEmail: order.billing.email,
  customerPhone: order.billing.phone,
  orderTotal: order.total,
  orderStatus: order.status,
  items: order.line_items.map(item => ({
    name: item.name,
    quantity: item.quantity,
    price: item.price,
    sku: item.sku
  })),
  deviceType: order.line_items[0].name.includes('iPhone') ? 'iPhone' : 
              order.line_items[0].name.includes('Samsung') ? 'Samsung' :
              order.line_items[0].name.includes('iPad') ? 'iPad' :
              order.line_items[0].name.includes('MacBook') ? 'MacBook' : 'Other',
  serviceType: order.line_items[0].name.includes('Screen') ? 'Screen Repair' :
               order.line_items[0].name.includes('Battery') ? 'Battery Replacement' :
               order.line_items[0].name.includes('Service') ? 'Repair Service' : 'Parts Order'
};

return { orderData };

