const status = $json.body.status;
const orderId = $json.body.orderId;
const customerEmail = $json.body.customerEmail;
const customerName = $json.body.customerName;
const deviceType = $json.body.deviceType;

let statusMessage, nextSteps;

switch(status) {
  case 'received':
    statusMessage = 'We\'ve received your device and our technicians are beginning the diagnostic process.';
    nextSteps = 'You\'ll receive a detailed repair estimate within 2 hours.';
    break;
  case 'diagnosed':
    statusMessage = 'Diagnostic complete! We\'ve identified the issues and prepared your repair estimate.';
    nextSteps = 'Please review the estimate in your customer portal and approve to proceed.';
    break;
  case 'approved':
    statusMessage = 'Repair approved! Our technicians are now working on your device.';
    nextSteps = 'Estimated completion time: 2-4 hours. We\'ll notify you when ready.';
    break;
  case 'in_progress':
    statusMessage = 'Your device repair is currently in progress.';
    nextSteps = 'Our skilled technicians are carefully working on your device.';
    break;
  case 'testing':
    statusMessage = 'Repair complete! We\'re now running comprehensive quality tests.';
    nextSteps = 'Final testing typically takes 30-60 minutes.';
    break;
  case 'completed':
    statusMessage = 'Great news! Your device repair is complete and ready for pickup.';
    nextSteps = 'Please bring your order confirmation to collect your device.';
    break;
  case 'ready_pickup':
    statusMessage = 'Your device is ready for pickup at our location.';
    nextSteps = 'Store hours: Mon-Fri 9AM-7PM, Sat 10AM-5PM. Bring photo ID.';
    break;
  default:
    statusMessage = 'Your repair status has been updated.';
    nextSteps = 'Contact us if you have any questions.';
}

return {
  orderId,
  customerEmail,
  customerName,
  deviceType,
  status,
  statusMessage,
  nextSteps
};

