const inquiry = $json.body.message.toLowerCase();
const customerName = $json.body.name;
const customerEmail = $json.body.email;
const subject = $json.body.subject || 'General Inquiry';

let category = 'general';
let priority = 'normal';
let autoResponse = '';

// Categorize based on keywords
if (inquiry.includes('screen') || inquiry.includes('display') || inquiry.includes('cracked')) {
  category = 'screen_repair';
  autoResponse = 'Thank you for your screen repair inquiry! Screen repairs typically take 1-2 hours and come with a lifetime warranty. Would you like to schedule a diagnostic?';
} else if (inquiry.includes('battery') || inquiry.includes('charging') || inquiry.includes('power')) {
  category = 'battery_issue';
  autoResponse = 'Thanks for contacting us about battery issues! Battery replacements usually take 30-45 minutes. We use only high-quality batteries with 6-month warranties.';
} else if (inquiry.includes('water') || inquiry.includes('liquid') || inquiry.includes('wet')) {
  category = 'water_damage';
  priority = 'high';
  autoResponse = 'Water damage requires immediate attention! Please power off your device and bring it in ASAP. Our liquid damage recovery service has a high success rate when treated quickly.';
} else if (inquiry.includes('price') || inquiry.includes('cost') || inquiry.includes('quote')) {
  category = 'pricing';
  autoResponse = 'Thanks for your pricing inquiry! We offer competitive rates with transparent pricing. Most repairs range from $49-$199. Would you like a free diagnostic and quote?';
} else if (inquiry.includes('warranty') || inquiry.includes('guarantee')) {
  category = 'warranty';
  autoResponse = 'All our repairs come with warranties! Screen repairs: Lifetime warranty, Battery replacements: 6 months, Other repairs: 90 days. We stand behind our work!';
} else if (inquiry.includes('appointment') || inquiry.includes('schedule') || inquiry.includes('book')) {
  category = 'scheduling';
  autoResponse = 'We\'d be happy to schedule your repair! Walk-ins welcome, or book online at midastechnical.com/book. Average wait time: 15 minutes.';
}

return {
  customerName,
  customerEmail,
  subject,
  inquiry: $json.body.message,
  category,
  priority,
  autoResponse
};

